import teamPic from '../pics/teamPic.png';
import beet from '../pics/beet.png';
import { useEffect, useRef, useState } from 'react';
import mosquito from '../pics/mosq.png';

const initRef = {
    beetX: 0, 
    beetY: 0, 
    mosX: 0, 
    moxY: 0
}

const Team = () => {
	const beetRef = useRef(null);
    const mosRef = useRef(null);
    const [refData, setRefData] = useState(initRef);

    const displayMosquitos = () => {
		const radius = 100;
		const step = (2 * Math.PI) / 7;
		const list = [];
		let angle = 0;
		for (let i = 1; i <= 7; i++) {
			list.push(
				<img
					key={i}
					className='w-1/5 absolute'
					style={{
						top: Math.round(
							beetX / 2 +
								radius * Math.sin(angle)
						),
						left:
							Math.round(
								beetY / 2 +
									radius * Math.cos(angle)
							)
					}}
					src={mosquito}
					alt='Mosquito'
                    ref={mosRef}
				/>
			);
			angle += step;
		}

		return list;
	};

    useEffect(() => {
        setRefData({beetX: beetRef.current.clientWidth, beetY: beetRef.current.clientHeight, mosX: mosRef.current.clientWidth, moxY: mosRef.current.clientHeight})
        console.log(beetRef)
    }, [])

	return (
		<div>
			<h1 className='text-center text-6xl m-2'>Team Members</h1>
			<div className='mx-20'>
				<img className='w-full' src={teamPic} alt='Team' />
			</div>
			<div className='w-1/2 mx-auto my-60 relative'>
				<img className='w-full h-full' src={beet} alt='Beetroot' ref={beetRef} />
                <div>{displayMosquitos()}</div>
			</div>
		</div>
	);
};

export default Team;
