import { useRef, useEffect, useState } from 'react';
import teamPic from '../pics/teamPic.png';
import beet from '../pics/beet.png';
import mosquito from '../pics/mosq.jpg';

const Team = () => {
    const [beetW, setBeetW] = useState(0);
    const [beetH, setBeetH] = useState(0);

    const beetrootRef = useRef(null);

	const displayMosquitos = () => {
        let angle = 0;
        const step = (2*Math.PI) / 7;
        const radius = 100;
		const list = [];
		for (let i = 1; i <= 7; i++) {
            let x = Math.round(beetW/2 + radius * Math.cos(angle));
            let y = Math.round(beetH/2 + radius * Math.sin(angle));
			list.push(
				<img key={i} className={`w-1/5 left-[${x}px] top-[${y}px]`} src={mosquito} alt='Mosquito' />
			);
            angle += step;
		}

		return list;
	};

    useEffect(() => {
        setBeetW(beetrootRef.current.clientWidth);
        setBeetH(beetrootRef.current.clientHeight);
    }, [])

	return (
		<div>
			<h1 className='text-center text-6xl m-2'>Team Members</h1>
			<div className='mx-20'>
				<img className='w-full' src={teamPic} alt='Team' />
			</div>
			<div className='w-1/2 mx-auto my-60' ref={beetrootRef}>
				<img className='w-full h-full' src={beet} alt='Beetroot' />
			</div>
			<div className='absolute'>{displayMosquitos()}</div>
		</div>
	);
};

export default Team;
