import teamPic from '../pics/teamPic.png';
import beet from '../pics/beet.png';
import mosquito from '../pics/mosq.png';
import { useEffect, useRef, useState } from 'react';

const Team = () => {
    const [beetw, setBeetw] = useState(0);
    const [beeth, setBeeth] = useState(0);
    const beetRef = useRef(null);

	const displayMosquitos = () => {
        const radius = 450;
        const step = (2*Math.PI) / 7;
		const list = [];
        let angle = 0;
		for (let i = 1; i <= 7; i++) {
			list.push(
				<img
					key={i}
					className='w-1/5 absolute'
					style={{ top: Math.round(beeth/2 + radius * Math.sin(angle) + 5), left: Math.round(beeth/2 + radius * Math.cos(angle)) - 100}}
					src={mosquito}
					alt='Mosquito'
				/>
			);
            angle += step;
		}

		return list;
	};

    useEffect(() => {
        setBeetw(beetRef.current.clientWidth);
        setBeeth(beetRef.current.clientHeight);
    }, [])

	return (
		<div>
			<h1 className='text-center text-6xl m-2'>Team Members</h1>
			<div className='mx-20'>
				<img className='w-full' src={teamPic} alt='Team' />
			</div>
			<div className='w-1/2 mx-auto my-60 relative' ref={beetRef}>
				<img className='w-full h-full' src={beet} alt='Beetroot' />
				{displayMosquitos()}
			</div>
		</div>
	);
};

export default Team;
