import mosquito from '../pics/mosq.png';
import beet from '../pics/beet.png';
import { useRef, useEffect, useState } from 'react';

const initRef = {
	beetX: 0,
	beetY: 0,
	mosX: 0,
	mosY: 0,
};

const MosquitoRoulette = () => {
	const beetRef = useRef(null);
	const mosRef = useRef(null);
	const [refData, setRefData] = useState(initRef);

	const displayMosquitos = () => {
		const list = [];
		for (let i = 1; i <= 7; i++) {
			list.push(
				<img
					key={i}
					className='w-1/5 absolute'
					style={{
                        transformOrigin: '200px'
					}}
					src={mosquito}
					alt='Mosquito'
					ref={mosRef}
				/>
			);
		}

		return list;
	};

	return (
		<div className='flex w-1/2 mx-auto my-60 relative items-center justify-center'>
			<img
				className='w-full h-full'
				src={beet}
				alt='Beetroot'
				ref={beetRef}
			/>
            {console.log(refData)}
			{displayMosquitos()}
		</div>
	);
};

export default MosquitoRoulette;
